      else:
        if cmd[0] == 'L':
          colorCode = cmd[int(((cmd.find(':') + 1) + 1) - 1) : int((cmd.rfind('#') + 1) - 1)]
          tiny_rgb.show(0, hex_to_rgb(colorCode))
        else:
          if cmd[0] == 'D':
            if cmd.find('1') + 1 != 0:
              pin4.servo_write(0)
            else:
              pin4.servo_write(90)
          else:
            if cmd[0] == 'W':
              if cmd.find('1') + 1 != 0:
                pin4.servo_write(90)
